import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

function Checkout() {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  
  const [formData, setFormData] = useState({
    fullName: '',
    address: '',
    city: '',
    postalCode: '',
    phone: '',
    paymentMethod: 'card'
  });

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = () => {
    try {
      const items = JSON.parse(localStorage.getItem('cartItems') || '[]');
      setCartItems(items);
    } catch (error) {
      console.error('Cart fetch error:', error);
      toast.error('Failed to load cart');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateSubtotal = () => {
    return cartItems.reduce((total, item) => {
      return total + (item.price * item.quantity);
    }, 0);
  };

  const calculateTotal = () => {
    const subtotal = calculateSubtotal();
    const shipping = 5.00;
    const tax = subtotal * 0.08;
    return subtotal + shipping + tax;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      // Validate required fields
      if (!formData.fullName.trim() || !formData.address.trim() || !formData.city.trim()) {
        toast.error('Please fill in all required fields');
        setSubmitting(false);
        return;
      }

      // Calculate totals
      const subtotal = calculateSubtotal();
      const shipping = 5.00;
      const tax = subtotal * 0.08;
      const totalAmount = subtotal + shipping + tax;

      // Create order object
      const order = {
        id: 'ORD-' + Date.now(),
        customer: {
          fullName: formData.fullName.trim(),
          address: formData.address.trim(),
          city: formData.city.trim(),
          postalCode: formData.postalCode.trim(),
          phone: formData.phone.trim()
        },
        paymentMethod: formData.paymentMethod,
        items: cartItems,
        totalAmount: totalAmount,
        date: new Date().toISOString(),
        status: 'completed'
      };

      // Save order to localStorage
      const existingOrders = JSON.parse(localStorage.getItem('orders') || '[]');
      existingOrders.push(order);
      localStorage.setItem('orders', JSON.stringify(existingOrders));

      // Clear cart
      localStorage.removeItem('cartItems');
      
      // Update navbar cart count
      const cartCountElement = document.querySelector('.cart-count-premium');
      if (cartCountElement) {
        cartCountElement.textContent = '0';
      }
      
      // Trigger cart update event
      window.dispatchEvent(new Event('cartUpdated'));

      toast.success('ðŸŽ‰ Order placed successfully! Thank you for your purchase.');

      // Navigate to home after 2 seconds
      setTimeout(() => {
        navigate('/home');
      }, 2000);
      
    } catch (error) {
      console.error('Order error:', error);
      toast.error('Failed to place order. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  // Fallback function for test order
  const submitTestOrder = () => {
    const order = {
      id: 'TEST-ORD-' + Date.now(),
      customer: {
        fullName: formData.fullName || 'Test Customer',
        address: formData.address || '123 Test St',
        city: formData.city || 'Test City',
        postalCode: formData.postalCode || '12345',
        phone: formData.phone || '1234567890'
      },
      paymentMethod: formData.paymentMethod,
      items: cartItems.length > 0 ? cartItems : [{
        id: 'test-product-1',
        name: 'Test Product',
        price: 10.99,
        quantity: 1
      }],
      totalAmount: calculateTotal() || 10.99,
      date: new Date().toISOString(),
      status: 'completed'
    };

    // Save test order to localStorage
    const existingOrders = JSON.parse(localStorage.getItem('orders') || '[]');
    existingOrders.push(order);
    localStorage.setItem('orders', JSON.stringify(existingOrders));

    // Clear cart
    localStorage.removeItem('cartItems');
    
    // Update navbar cart count
    const cartCountElement = document.querySelector('.cart-count-premium');
    if (cartCountElement) {
      cartCountElement.textContent = '0';
    }
    
    // Trigger cart update event
    window.dispatchEvent(new Event('cartUpdated'));

    toast.success('ðŸŽ‰ Test order placed successfully!');
    
    setTimeout(() => {
      navigate('/home');
    }, 2000);
  };

  if (cartItems.length === 0) {
    return (
      <Container className="py-5">
        <Alert variant="warning">
          <Alert.Heading>Your cart is empty!</Alert.Heading>
          <p>Add some items to your cart before checkout.</p>
        </Alert>
        <Button variant="primary" onClick={() => navigate('/shop')}>
          Go Shopping
        </Button>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <h1 className="mb-4">Checkout</h1>
      
      <Row>
        <Col lg={8}>
          <Card className="mb-4 shadow">
            <Card.Header>
              <h5 className="mb-0">Shipping Information</h5>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleSubmit}>
                <Row className="mb-3">
                  <Form.Group as={Col} controlId="fullName">
                    <Form.Label>Full Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleChange}
                      required
                      placeholder="Enter your full name"
                    />
                  </Form.Group>

                  <Form.Group as={Col} controlId="phone">
                    <Form.Label>Phone Number *</Form.Label>
                    <Form.Control
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleChange}
                      required
                      placeholder="Enter phone number"
                    />
                  </Form.Group>
                </Row>

                <Form.Group className="mb-3" controlId="address">
                  <Form.Label>Address *</Form.Label>
                  <Form.Control
                    type="text"
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    required
                    placeholder="Enter full address"
                  />
                </Form.Group>

                <Row className="mb-3">
                  <Form.Group as={Col} controlId="city">
                    <Form.Label>City *</Form.Label>
                    <Form.Control
                      type="text"
                      name="city"
                      value={formData.city}
                      onChange={handleChange}
                      required
                      placeholder="Enter city"
                    />
                  </Form.Group>

                  <Form.Group as={Col} controlId="postalCode">
                    <Form.Label>Postal Code *</Form.Label>
                    <Form.Control
                      type="text"
                      name="postalCode"
                      value={formData.postalCode}
                      onChange={handleChange}
                      required
                      placeholder="Enter postal code"
                    />
                  </Form.Group>
                </Row>

                <Form.Group className="mb-4" controlId="paymentMethod">
                  <Form.Label>Payment Method *</Form.Label>
                  <div>
                    <Form.Check
                      type="radio"
                      label="Credit/Debit Card"
                      name="paymentMethod"
                      value="card"
                      checked={formData.paymentMethod === 'card'}
                      onChange={handleChange}
                      className="mb-2"
                    />
                    <Form.Check
                      type="radio"
                      label="Cash on Delivery"
                      name="paymentMethod"
                      value="cash"
                      checked={formData.paymentMethod === 'cash'}
                      onChange={handleChange}
                    />
                  </div>
                </Form.Group>

                <div className="d-grid">
                  <Button 
                    variant="primary" 
                    size="lg" 
                    type="submit"
                    disabled={submitting}
                  >
                    {submitting ? 'Placing Order...' : 'Place Order'}
                  </Button>
                  
                  {/* Test button for debugging */}
                  <Button 
                    variant="outline-secondary" 
                    size="sm" 
                    className="mt-2"
                    onClick={submitTestOrder}
                    disabled={submitting}
                  >
                    Place Test Order (Debug)
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
        
        <Col lg={4}>
          <Card className="shadow">
            <Card.Header>
              <h5 className="mb-0">Order Summary</h5>
            </Card.Header>
            <Card.Body>
              <div className="mb-3">
                <h6>Items in Cart ({cartItems.length}):</h6>
                {cartItems.map(item => (
                  <div key={item.id} className="d-flex justify-content-between mb-2">
                    <span>
                      {item.name} Ã— {item.quantity}
                    </span>
                    <span>
                      ${(item.price * item.quantity).toFixed(2)}
                    </span>
                  </div>
                ))}
              </div>
              
              <div className="border-top pt-3">
                <div className="d-flex justify-content-between mb-2">
                  <span>Subtotal</span>
                  <span>${calculateSubtotal().toFixed(2)}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Shipping</span>
                  <span>$5.00</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Tax (8%)</span>
                  <span>${(calculateSubtotal() * 0.08).toFixed(2)}</span>
                </div>
                <div className="d-flex justify-content-between fw-bold fs-5">
                  <span>Total</span>
                  <span>${calculateTotal().toFixed(2)}</span>
                </div>
              </div>
              
              <Alert variant="info" className="mt-3">
                <small>
                  * Required fields must be filled to place order.
                </small>
              </Alert>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default Checkout;
