import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { toast } from 'react-toastify';
import './../App.css';

// Your local products data
const localProducts = [
  {
    _id: 'donut1',
    name: 'Classic Glazed Donut',
    description: 'Our signature donut with sweet vanilla glaze',
    price: 3.99,
    category: 'donut',
    featured: true,
    tags: ['BEST SELLER', 'CLASSIC']
  },
  {
    _id: 'donut2',
    name: 'Chocolate Sprinkle Donut',
    description: 'Rich chocolate frosting with rainbow sprinkles',
    price: 4.49,
    category: 'donut',
    featured: true,
    tags: ['CHOCOLATE', 'KID FAVORITE']
  },
  {
    _id: 'donut3',
    name: 'Strawberry Frosted Donut',
    description: 'Pink strawberry glaze with fresh berry notes',
    price: 4.29,
    category: 'donut',
    featured: false,
    tags: ['FRUITY', 'PINK']
  },
  {
    _id: 'donut4',
    name: 'Boston Cream Donut',
    description: 'Custard filled with chocolate ganache',
    price: 4.99,
    category: 'donut',
    featured: false,
    tags: ['CREAM FILLED', 'CHOCOLATE']
  },
  {
    _id: 'donut5',
    name: 'Maple Bacon Donut',
    description: 'Sweet maple glaze with crispy bacon bits',
    price: 5.99,
    category: 'donut',
    featured: true,
    tags: ['PREMIUM', 'SALTY SWEET']
  },
  {
    _id: 'donut6',
    name: 'Blueberry Blast Donut',
    description: 'Blueberry infused donut with cream cheese filling',
    price: 4.79,
    category: 'donut',
    featured: false,
    tags: ['FRUIT FILLED', 'BLUEBERRY']
  },
  {
    _id: 'donut7',
    name: 'Cinnamon Sugar Donut',
    description: 'Warm cinnamon and sugar coating',
    price: 3.79,
    category: 'donut',
    featured: false,
    tags: ['CLASSIC', 'CINNAMON']
  },
  {
    _id: 'donut8',
    name: 'Matcha Green Tea Donut',
    description: 'Japanese matcha glaze with white chocolate drizzle',
    price: 5.49,
    category: 'donut',
    featured: true,
    tags: ['PREMIUM', 'ASIAN FLAVOR']
  },
  {
    _id: 'donut9',
    name: 'Red Velvet Donut',
    description: 'Red velvet cake donut with cream cheese frosting',
    price: 4.99,
    category: 'donut',
    featured: false,
    tags: ['CAKE DONUT', 'RED VELVET']
  },
  {
    _id: 'donut10',
    name: 'Salted Caramel Donut',
    description: 'Salted caramel glaze with toffee pieces',
    price: 5.29,
    category: 'donut',
    featured: true,
    tags: ['CARAMEL', 'PREMIUM']
  },
  {
    _id: 'donut11',
    name: 'Cookies & Cream Donut',
    description: 'Oreo cookie crust with vanilla cream filling',
    price: 5.99,
    category: 'donut',
    featured: true,
    tags: ['CREAM FILLED', 'COOKIE']
  }
];

function Shop() {
  const { category } = useParams();
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchProducts();
  }, [category]);

  const fetchProducts = () => {
    setLoading(true);
    
    // Filter products by category if specified
    let filtered = category 
      ? localProducts.filter(product => product.category === category)
      : localProducts;
    
    setFilteredProducts(filtered);
    setLoading(false);
  };

  const handleAddToCart = (product) => {
    try {
      // Get existing cart from localStorage
      const cartItems = JSON.parse(localStorage.getItem('cartItems') || '[]');
      
      // Check if product already exists in cart
      const existingItem = cartItems.find(item => item.id === product._id);
      
      if (existingItem) {
        existingItem.quantity += 1;
      } else {
        cartItems.push({
          id: product._id,
          name: product.name,
          price: product.price,
          quantity: 1,
          image: `/images/donuts/${product._id.replace('donut', '')}.png`
        });
      }
      
      // Save back to localStorage
      localStorage.setItem('cartItems', JSON.stringify(cartItems));
      
      // Show success toast
      toast.success(`${product.name} added to cart! üõí`);
      
      // Update cart count (you might have a cart context or state)
      // This triggers a re-render if you're watching cartItems
      
    } catch (error) {
      console.error('Error adding to cart:', error);
      toast.error('Failed to add item to cart');
    }
  };

  const getCategoryTitle = () => {
    if (!category) return 'All Products';
    
    const categoryNames = {
      'donut': 'Donuts üç©',
      'fancies': 'Fancies üéÇ',
      'pizza': 'Pizza üçï',
      'beverage': 'Beverages ‚òï',
      'drink': 'Drinks ü•§'
    };
    
    return categoryNames[category] || `${category.charAt(0).toUpperCase() + category.slice(1)}`;
  };

  const getImageUrl = (productId) => {
    // Map product IDs to image filenames
    const imageMap = {
      'donut1': 'classic-glazed.png',
      'donut2': 'chocolate.png',
      'donut3': 'strawberry.png',
      'donut4': 'boston-cream.png',
      'donut5': 'maple-bacon.png',
      'donut6': 'blueberry.png',
      'donut7': 'cinnamon.png',
      'donut8': 'matcha.png',
      'donut9': 'red-velvet.png',
      'donut10': 'salted-caramel.png',
      'donut11': 'cookies-cream.png'
    };
    
    const filename = imageMap[productId] || 'default.png';
    return `/images/donuts/${filename}`;
  };

  return (
    <div className="shop-page">
      {/* Category Banner */}
      <div className="category-banner">
        <div className="category-banner-content">
          <h1>{getCategoryTitle()}</h1>
          <p>Discover our delicious {category || 'donut'} collection</p>
        </div>
      </div>

      <Container>
        {/* Categories Navigation */}
        <div className="categories-nav">
          <a 
            href="/shop/donut" 
            className={`category-tab ${category === 'donut' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/donut';
            }}
          >
            <span className="category-icon">üç©</span>
            <span>Donuts</span>
          </a>
          <a 
            href="/shop/fancies" 
            className={`category-tab ${category === 'fancies' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/fancies';
            }}
          >
            <span className="category-icon">üéÇ</span>
            <span>Fancies</span>
          </a>
          <a 
            href="/shop/pizza" 
            className={`category-tab ${category === 'pizza' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/pizza';
            }}
          >
            <span className="category-icon">üçï</span>
            <span>Pizza</span>
          </a>
          <a 
            href="/shop/beverage" 
            className={`category-tab ${category === 'beverage' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/beverage';
            }}
          >
            <span className="category-icon">‚òï</span>
            <span>Beverages</span>
          </a>
          <a 
            href="/shop/drink" 
            className={`category-tab ${category === 'drink' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/drink';
            }}
          >
            <span className="category-icon">ü•§</span>
            <span>Drinks</span>
          </a>
        </div>

        {loading ? (
          <div className="text-center py-5">
            <div className="loading-spinner"></div>
            <p className="mt-3">Loading delicious treats...</p>
          </div>
        ) : filteredProducts.length === 0 ? (
          <div className="text-center py-5">
            <h4>No products found in this category</h4>
            <p className="text-muted">Check back later for new items!</p>
          </div>
        ) : (
          <>
            <div className="product-count mb-4">
              Showing {filteredProducts.length} {category || 'products'}
            </div>
            
            <Row xs={1} md={2} lg={3} xl={4} className="g-4">
              {filteredProducts.map(product => (
                <Col key={product._id}>
                  <div className="product-card-shop">
                    <div className="product-image-container">
                      <img 
                        src={getImageUrl(product._id)}
                        alt={product.name}
                        className="product-image-shop"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = "https://images.unsplash.com/photo-1551106652-a5bcf4b29ab6?auto=format&fit=crop&w=500";
                        }}
                      />
                      {product.featured && (
                        <span className="featured-badge-shop">FEATURED</span>
                      )}
                      {product.tags && product.tags.includes('BEST SELLER') && (
                        <span className="best-seller-badge-shop">BEST SELLER</span>
                      )}
                    </div>
                    <div className="product-info-shop">
                      <h3>{product.name}</h3>
                      <p className="product-description-shop">{product.description}</p>
                      
                      <div className="product-tags">
                        {product.tags && product.tags.map((tag, index) => (
                          <span key={index} className="product-tag">{tag}</span>
                        ))}
                      </div>
                      
                      <div className="product-footer-shop">
                        <span className="product-price-shop">${product.price}</span>
                        <button 
                          className="add-to-cart-btn-shop"
                          onClick={() => handleAddToCart(product)}
                        >
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  </div>
                </Col>
              ))}
            </Row>
          </>
        )}
      </Container>
    </div>
  );
}

export default Shop;
