import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Card, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

function Checkout() {
  const navigate = useNavigate();
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  
  const [formData, setFormData] = useState({
    fullName: '',
    address: '',
    city: '',
    postalCode: '',
    phone: '',
    paymentMethod: 'card'
  });

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
      const response = await axios.get(`${API_URL}/cart`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data);
    } catch (error) {
      console.error('Cart fetch error:', error);
      toast.error('Failed to load cart');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const calculateSubtotal = () => {
    if (!cart || !cart.items) return 0;
    return cart.items.reduce((total, item) => {
      const price = item.productId?.price || 0;
      const quantity = item.quantity || 0;
      return total + (price * quantity);
    }, 0);
  };

  const calculateTotal = () => {
    const subtotal = calculateSubtotal();
    const shipping = 5.00;
    const tax = subtotal * 0.08;
    return subtotal + shipping + tax;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const token = localStorage.getItem('token');
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
      
      // Validate required fields
      if (!formData.fullName.trim() || !formData.address.trim() || !formData.city.trim()) {
        toast.error('Please fill in all required fields');
        setSubmitting(false);
        return;
      }

      // Prepare cart items for order
      const orderItems = cart?.items?.map(item => ({
        productId: item.productId?._id || 'unknown-product',
        name: item.productId?.name || 'Product',
        price: item.productId?.price || 0,
        quantity: item.quantity || 1
      })) || [];

      // Calculate totals
      const subtotal = calculateSubtotal();
      const shipping = 5.00;
      const tax = subtotal * 0.08;
      const totalAmount = subtotal + shipping + tax;

      // Prepare complete order data
      const orderData = {
        shippingAddress: {
          fullName: formData.fullName.trim(),
          address: formData.address.trim(),
          city: formData.city.trim(),
          postalCode: formData.postalCode.trim(),
          phone: formData.phone.trim()
        },
        paymentMethod: formData.paymentMethod,
        items: orderItems,
        totalAmount: totalAmount
      };

      console.log('Sending order data:', orderData);

      // First try regular endpoint
      const response = await axios.post(
        `${API_URL}/orders`,
        orderData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          } 
        }
      );

      console.log('Order response:', response.data);

      if (response.data.success) {
        toast.success('Order placed successfully! ðŸŽ‰');
        
        // Clear cart after successful order
        try {
          await axios.delete(`${API_URL}/cart`, {
            headers: { Authorization: `Bearer ${token}` }
          });
        } catch (cartError) {
          console.log('Cart clear error:', cartError);
        }
        
        // Redirect to home
        setTimeout(() => {
          navigate('/home');
        }, 2000);
      } else {
        toast.error(response.data.error || 'Failed to place order');
      }
      
    } catch (error) {
      console.error('Order error details:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      
      // If regular endpoint fails, try test endpoint
      if (error.response?.status === 500) {
        toast.info('Trying alternative method...');
        try {
          await submitTestOrder();
        } catch (testError) {
          toast.error('Failed to place order. Please try again.');
        }
      } else if (error.response?.status === 400) {
        toast.error(`Validation error: ${error.response.data?.error || 'Invalid data'}`);
      } else if (error.response?.status === 401) {
        toast.error('Please login again');
        navigate('/login');
      } else {
        toast.error(error.response?.data?.error || 'Network error. Please try again.');
      }
    } finally {
      setSubmitting(false);
    }
  };

  // Fallback function for test order
  const submitTestOrder = async () => {
    const token = localStorage.getItem('token');
    const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
    
    const testOrderData = {
      shippingAddress: {
        fullName: formData.fullName || 'Test Customer',
        address: formData.address || '123 Test St',
        city: formData.city || 'Test City',
        postalCode: formData.postalCode || '12345',
        phone: formData.phone || '1234567890'
      },
      paymentMethod: formData.paymentMethod,
      items: [
        {
          productId: 'test-product-1',
          name: 'Test Product',
          price: 10.99,
          quantity: 1
        }
      ],
      totalAmount: 10.99
    };

    const response = await axios.post(
      `${API_URL}/orders/test`, // Use test endpoint
      testOrderData,
      { 
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        } 
      }
    );

    toast.success('Test order placed successfully!');
    setTimeout(() => {
      navigate('/home');
    }, 2000);
  };

  if (loading) {
    return (
      <Container className="py-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </Container>
    );
  }

  if (!cart || !cart.items || cart.items.length === 0) {
    return (
      <Container className="py-5">
        <Alert variant="warning">
          <Alert.Heading>Your cart is empty!</Alert.Heading>
          <p>Add some items to your cart before checkout.</p>
        </Alert>
        <Button variant="primary" onClick={() => navigate('/shop')}>
          Go Shopping
        </Button>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <h1 className="mb-4">Checkout</h1>
      
      <Row>
        <Col lg={8}>
          <Card className="mb-4 shadow">
            <Card.Header>
              <h5 className="mb-0">Shipping Information</h5>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleSubmit}>
                <Row className="mb-3">
                  <Form.Group as={Col} controlId="fullName">
                    <Form.Label>Full Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleChange}
                      required
                      placeholder="Enter your full name"
                    />
                  </Form.Group>

                  <Form.Group as={Col} controlId="phone">
                    <Form.Label>Phone Number *</Form.Label>
                    <Form.Control
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleChange}
                      required
                      placeholder="Enter phone number"
                    />
                  </Form.Group>
                </Row>

                <Form.Group className="mb-3" controlId="address">
                  <Form.Label>Address *</Form.Label>
                  <Form.Control
                    type="text"
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    required
                    placeholder="Enter full address"
                  />
                </Form.Group>

                <Row className="mb-3">
                  <Form.Group as={Col} controlId="city">
                    <Form.Label>City *</Form.Label>
                    <Form.Control
                      type="text"
                      name="city"
                      value={formData.city}
                      onChange={handleChange}
                      required
                      placeholder="Enter city"
                    />
                  </Form.Group>

                  <Form.Group as={Col} controlId="postalCode">
                    <Form.Label>Postal Code *</Form.Label>
                    <Form.Control
                      type="text"
                      name="postalCode"
                      value={formData.postalCode}
                      onChange={handleChange}
                      required
                      placeholder="Enter postal code"
                    />
                  </Form.Group>
                </Row>

                <Form.Group className="mb-4" controlId="paymentMethod">
                  <Form.Label>Payment Method *</Form.Label>
                  <div>
                    <Form.Check
                      type="radio"
                      label="Credit/Debit Card"
                      name="paymentMethod"
                      value="card"
                      checked={formData.paymentMethod === 'card'}
                      onChange={handleChange}
                      className="mb-2"
                    />
                    <Form.Check
                      type="radio"
                      label="Cash on Delivery"
                      name="paymentMethod"
                      value="cash"
                      checked={formData.paymentMethod === 'cash'}
                      onChange={handleChange}
                    />
                  </div>
                </Form.Group>

                <div className="d-grid">
                  <Button 
                    variant="primary" 
                    size="lg" 
                    type="submit"
                    disabled={submitting}
                  >
                    {submitting ? 'Placing Order...' : 'Place Order'}
                  </Button>
                  
                  {/* Test button for debugging */}
                  <Button 
                    variant="outline-secondary" 
                    size="sm" 
                    className="mt-2"
                    onClick={submitTestOrder}
                    disabled={submitting}
                  >
                    Place Test Order (Debug)
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
        
        <Col lg={4}>
          <Card className="shadow">
            <Card.Header>
              <h5 className="mb-0">Order Summary</h5>
            </Card.Header>
            <Card.Body>
              <div className="mb-3">
                <h6>Items in Cart ({cart.items.length}):</h6>
                {cart.items.map(item => (
                  <div key={item._id} className="d-flex justify-content-between mb-2">
                    <span>
                      {item.productId?.name || 'Product'} Ã— {item.quantity}
                    </span>
                    <span>
                      ${((item.productId?.price || 0) * (item.quantity || 1)).toFixed(2)}
                    </span>
                  </div>
                ))}
              </div>
              
              <div className="border-top pt-3">
                <div className="d-flex justify-content-between mb-2">
                  <span>Subtotal</span>
                  <span>${calculateSubtotal().toFixed(2)}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Shipping</span>
                  <span>$5.00</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Tax (8%)</span>
                  <span>${(calculateSubtotal() * 0.08).toFixed(2)}</span>
                </div>
                <div className="d-flex justify-content-between fw-bold fs-5">
                  <span>Total</span>
                  <span>${calculateTotal().toFixed(2)}</span>
                </div>
              </div>
              
              <Alert variant="info" className="mt-3">
                <small>
                  * Required fields must be filled to place order.
                  {cart.items.some(item => !item.productId?.price) && 
                    " Note: Some items may have missing price information."}
                </small>
              </Alert>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default Checkout;
