// pages/Shop.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import { toast } from 'react-toastify';
import axios from 'axios';
import { products } from './../products';
import './../App.css';

function Shop() {
  const { category } = useParams();
  const [allProducts, setAllProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('default');
  
  const categories = [
    { id: 'all', name: 'All Products', icon: 'ðŸ½ï¸' },
    { id: 'donut', name: 'Donuts', icon: 'ðŸ©' },
    { id: 'fancies', name: 'Fancies', icon: 'ðŸŽ‚' },
    { id: 'pizza', name: 'Pizza', icon: 'ðŸ•' },
    { id: 'beverage', name: 'Beverages', icon: 'â˜•' },
    { id: 'drink', name: 'Drinks', icon: 'ðŸ¥¤' }
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    filterAndSortProducts();
  }, [category, allProducts, searchTerm, sortBy]);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/products`);
      
      if (response.data && response.data.length > 0) {
        setAllProducts(response.data);
      } else {
        // Use local products if API fails
        setAllProducts(products);
      }
    } catch (error) {
      console.error('Error fetching products:', error);
      // Use local products
      setAllProducts(products);
    } finally {
      setLoading(false);
    }
  };

  const filterAndSortProducts = () => {
    let filtered = [...allProducts];
    
    // Filter by category
    if (category && category !== 'all') {
      filtered = filtered.filter(product => product.category === category);
    }
    
    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (product.tags && product.tags.some(tag => 
          tag.toLowerCase().includes(searchTerm.toLowerCase())
        ))
      );
    }
    
    // Sort products
    switch (sortBy) {
      case 'price-low':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        filtered.sort((a, b) => b.price - a.price);
        break;
      case 'name':
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      default:
        // Default sorting (featured first, then by id)
        filtered.sort((a, b) => {
          if (a.featured && !b.featured) return -1;
          if (!a.featured && b.featured) return 1;
          return 0;
        });
    }
    
    setFilteredProducts(filtered);
  };

  const handleAddToCart = async (product) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        toast.error('Please login to add items to cart!');
        return;
      }
      
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/cart`,
        { 
          productId: product._id, 
          quantity: 1 
        },
        { 
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          } 
        }
      );
      
      toast.success(`${product.name} added to cart! ðŸ›’`);
      
    } catch (error) {
      console.error('Add to cart error:', error);
      // Fallback to localStorage for demo
      const cartItems = JSON.parse(localStorage.getItem('cartItems') || '[]');
      cartItems.push({
        id: product._id,
        name: product.name,
        price: product.price,
        quantity: 1
      });
      localStorage.setItem('cartItems', JSON.stringify(cartItems));
      toast.success(`${product.name} added to cart! ðŸ›’`);
    }
  };

  const getCategoryName = () => {
    if (!category || category === 'all') return 'All Products';
    const cat = categories.find(c => c.id === category);
    return cat ? cat.name : 'Products';
  };

  return (
    <div className="shop-page">
      {/* Shop Header */}
      <div className="shop-header">
        <h1>{getCategoryName()}</h1>
        <p>Discover our delicious {getCategoryName().toLowerCase()}</p>
      </div>

      <Container>
        {/* Categories Navigation */}
        <div className="categories-nav">
          {categories.map(cat => (
            <Link
              key={cat.id}
              to={`/shop/${cat.id}`}
              className={`category-tab ${(!category && cat.id === 'all') || category === cat.id ? 'active' : ''}`}
            >
              <span className="category-icon">{cat.icon}</span>
              <span>{cat.name}</span>
            </Link>
          ))}
        </div>

        {/* Search and Sort Bar */}
        <Row className="mb-4">
          <Col md={6}>
            <Form.Control
              type="text"
              placeholder="Search products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-bar"
            />
          </Col>
          <Col md={6} className="text-md-end">
            <Form.Select 
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="sort-select"
            >
              <option value="default">Sort by: Featured</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="name">Name: A to Z</option>
            </Form.Select>
          </Col>
        </Row>

        {/* Products Grid */}
        {loading ? (
          <div className="text-center py-5">
            <div className="loading-spinner"></div>
            <p className="mt-3">Loading products...</p>
          </div>
        ) : (
          <>
            {filteredProducts.length > 0 ? (
              <Row>
                {filteredProducts.map(product => (
                  <Col key={product._id} lg={3} md={4} sm={6} className="mb-4">
                    <div className="product-card-shop">
                      <div className="product-image-container">
                        <img 
                          src={product.image} 
                          alt={product.name}
                          className="product-image-shop"
                          onError={(e) => {
                            e.target.onerror = null;
                            e.target.src = "https://images.unsplash.com/photo-1551106652-a5bcf4b29ab6?auto=format&fit=crop&w=500";
                          }}
                        />
                        {product.featured && (
                          <span className="featured-badge">FEATURED</span>
                        )}
                      </div>
                      <div className="product-info-shop">
                        <h3>{product.name}</h3>
                        <p className="product-description-shop">{product.description}</p>
                        
                        <div className="product-tags">
                          {product.tags && product.tags.map(tag => (
                            <span key={tag} className="product-tag">{tag}</span>
                          ))}
                        </div>
                        
                        <div className="product-footer-shop">
                          <span className="product-price-shop">${product.price}</span>
                          <button 
                            className="add-to-cart-btn-shop"
                            onClick={() => handleAddToCart(product)}
                          >
                            Add to Cart
                          </button>
                        </div>
                      </div>
                    </div>
                  </Col>
                ))}
              </Row>
            ) : (
              <div className="text-center py-5">
                <h3>No products found</h3>
                <p>Try a different search or category</p>
                <Link to="/shop/all" className="cta-btn">VIEW ALL PRODUCTS</Link>
              </div>
            )}
          </>
        )}

        {/* Product Count */}
        {!loading && filteredProducts.length > 0 && (
          <div className="product-count">
            Showing {filteredProducts.length} of {allProducts.length} products
          </div>
        )}
      </Container>
    </div>
  );
}

export default Shop;
