import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import ProductCard from '../components/ProductCard';
import './../App.css';

function Home() {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories] = useState([
    { id: 'donut', name: 'Donuts', icon: 'üç©', count: 11 },
    { id: 'fancies', name: 'Fancies', icon: 'üéÇ', count: 7 },
    { id: 'pizza', name: 'Pizza', icon: 'üçï', count: 2 },
    { id: 'beverage', name: 'Beverages', icon: '‚òï', count: 5 },
    { id: 'drink', name: 'Drinks', icon: 'ü•§', count: 8 }
  ]);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/products`);
      // Get first 8 products as featured
      const featured = response.data.slice(0, 8).map(product => ({
        ...product,
        badge: product.category === 'donut' ? 'BEST SELLER' : 
               product.category === 'fancies' ? 'PREMIUM' : 'NEW'
      }));
      setFeaturedProducts(featured);
    } catch (error) {
      console.error('Error fetching products:', error);
      // Mock data for demo
      setFeaturedProducts(getMockProducts());
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = async (product) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        toast.error('Please login to add items to cart!');
        return;
      }
      
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/cart`,
        { 
          productId: product._id, 
          quantity: 1 
        },
        { 
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          } 
        }
      );
      
      toast.success(`${product.name} added to cart! üõí`);
      
      // Update cart count
      const cartItems = JSON.parse(localStorage.getItem('cartItems') || '[]');
      cartItems.push(product._id);
      localStorage.setItem('cartItems', JSON.stringify(cartItems));
      
    } catch (error) {
      console.error('Add to cart error:', error);
      toast.error('Added to cart (demo mode)');
    }
  };

  const getMockProducts = () => {
    return [
      {
        _id: '1',
        name: 'Unicorn Glaze Donut',
        description: 'Magical rainbow glaze with sprinkles',
        price: 4.99,
        category: 'donut',
        image: 'https://images.unsplash.com/photo-1571115764595-644a1f56a55c',
        badge: 'BEST SELLER'
      },
      {
        _id: '2',
        name: 'Red Velvet Fancy',
        description: 'Luxurious red velvet with cream cheese',
        price: 8.99,
        category: 'fancies',
        image: 'https://images.unsplash.com/photo-1550617931-e17a7b70dce2',
        badge: 'PREMIUM'
      },
      // Add more mock products...
    ];
  };

  return (
    <div className="home-premium">
      {/* Hero Section */}
      <section className="hero-section">
        <video autoPlay muted loop className="hero-video">
          <source src="/hero-video.mp4" type="video/mp4" />
          <img src="https://images.unsplash.com/photo-1551024506-0bccd828d307" alt="Donut Hero" />
        </video>
        
        <div className="hero-overlay">
          <h1 className="hero-title">DRUNK DONUTS</h1>
          <p className="hero-subtitle">
            Where every bite is a celebration! Experience our handcrafted donuts, 
            premium fancies, and delicious treats made with love and premium ingredients.
          </p>
          <div className="hero-cta">
            <Link to="/shop/donut" className="cta-btn">
              ORDER NOW
            </Link>
            <Link to="/shop" className="cta-btn cta-btn-secondary">
              VIEW MENU
            </Link>
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="menu-section-premium">
        <div className="section-header">
          <h2 className="section-title-premium">Our Categories</h2>
        </div>
        
        <Row className="justify-content-center g-4">
          {categories.map(category => (
            <Col md={4} lg={2} key={category.id}>
              <Link to={`/shop/${category.id}`} className="category-card">
                <div className="category-icon">{category.icon}</div>
                <h3>{category.name}</h3>
                <p>{category.count} items</p>
              </Link>
            </Col>
          ))}
        </Row>
      </section>

      {/* Featured Products */}
      <section className="menu-section-premium">
        <div className="section-header">
          <h2 className="section-title-premium">Featured Treats</h2>
          <p className="section-subtitle">Our most popular creations</p>
        </div>

        {loading ? (
          <div className="text-center py-5">
            <div className="loading-spinner"></div>
            <p className="mt-3">Loading delicious treats...</p>
          </div>
        ) : (
          <div className="menu-grid-premium">
            {featuredProducts.map(product => (
              <div key={product._id} className="menu-item-premium">
                {product.badge && (
                  <span className="product-badge">{product.badge}</span>
                )}
                <div className="product-image-container">
                  <img 
                    src={product.image} 
                    alt={product.name}
                    className="product-image"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = "https://images.unsplash.com/photo-1551106652-a5bcf4b29ab6";
                    }}
                  />
                </div>
                <h3 className="product-name">{product.name}</h3>
                <p className="product-description">{product.description}</p>
                <div className="product-price-section">
                  <span className="product-price">${product.price}</span>
                  <div className="product-rating">
                    ‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê (24)
                  </div>
                </div>
                <button 
                  className="add-btn-premium"
                  onClick={() => handleAddToCart(product)}
                >
                  <span>ADD TO CART</span>
                </button>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Special Offer Banner */}
      <section className="menu-section-premium">
        <div className="banner-premium">
          <img 
            src="https://images.unsplash.com/photo-1513104890138-7c749659a591" 
            alt="Special Offer" 
            className="banner-img-premium"
          />
          <div className="banner-overlay">
            <h2>Weekend Special! üéâ</h2>
            <p>Get 20% off on all fancies this weekend</p>
            <Link to="/shop/fancies" className="cta-btn">GRAB DEAL</Link>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Home;
