import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import ProductCard from '../components/ProductCard';

// ADD THE HOME COMPONENT FUNCTION HERE:
function Home() {
  const navigate = useNavigate();
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/products`);
      // Get first 6 products as featured
      setFeaturedProducts(response.data.slice(0, 6));
    } catch (error) {
      toast.error('Failed to load products');
    } finally {
      setLoading(false);
    }
  };

  // YOUR handleAddToCart FUNCTION (KEEP THIS):
  const handleAddToCart = async (product) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        toast.error('Please login first!');
        navigate('/login');
        return;
      }
      
      console.log('Adding to cart:', product._id);
      
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/cart`,
        { 
          productId: product._id, 
          quantity: 1 
        },
        { 
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          } 
        }
      );
      
      console.log('Cart response:', response.data);
      
      if (response.status === 201 || response.status === 200) {
        toast.success(`${product.name} added to cart!`);
      } else {
        toast.error('Failed to add to cart');
      }
      
    } catch (error) {
      console.error('Add to cart error:', error.response || error);
      
      // Show helpful error message
      if (error.response) {
        if (error.response.status === 401) {
          toast.error('Session expired. Please login again.');
          localStorage.removeItem('token');
          navigate('/login');
        } else if (error.response.status === 404) {
          toast.error('Product not found');
        } else {
          toast.error(`Error: ${error.response.data?.error || 'Failed to add to cart'}`);
        }
      } else if (error.request) {
        toast.error('Network error. Check if backend is running.');
      } else {
        toast.error('Error: ' + error.message);
      }
    }
  };

  // ADD THE RETURN/JSX PART:
  return (
    <Container>
      {/* Hero Section */}
      <Row className="py-5 mb-5 text-center bg-light rounded">
        <Col>
          <h1 className="display-4 fw-bold">Welcome to Donut Shop! üç©</h1>
          <p className="lead">
            Freshly baked donuts, delicious beverages, cheesy pizzas, and refreshing drinks.
            Everything you need for a sweet treat!
          </p>
          <Button as={Link} to="/shop/donut" variant="primary" size="lg" className="mt-3">
            Start Shopping ‚Üí
          </Button>
        </Col>
      </Row>

      {/* Featured Products */}
      <Row className="mb-5">
        <Col>
          <h2 className="mb-4">Featured Products</h2>
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
            </div>
          ) : (
            <Row xs={1} md={2} lg={3} className="g-4">
              {featuredProducts.map(product => (
                <Col key={product._id}>
                  <ProductCard 
                    product={product} 
                    onAddToCart={handleAddToCart}
                  />
                </Col>
              ))}
            </Row>
          )}
        </Col>
      </Row>

      {/* Categories Section */}
      <Row className="mb-5">
        <Col>
          <h2 className="mb-4">Shop by Category</h2>
          <Row className="g-4">
            <Col md={3}>
              <div className="card text-center h-100 shadow-sm">
                <div className="card-body">
                  <h3>üç©</h3>
                  <h5>Donuts</h5>
                  <p>Sweet and delicious</p>
                  <Button as={Link} to="/shop/donut" variant="outline-primary">
                    Browse
                  </Button>
                </div>
              </div>
            </Col>
            <Col md={3}>
              <div className="card text-center h-100 shadow-sm">
                <div className="card-body">
                  <h3>‚òï</h3>
                  <h5>Beverages</h5>
                  <p>Hot & cold drinks</p>
                  <Button as={Link} to="/shop/beverage" variant="outline-primary">
                    Browse
                  </Button>
                </div>
              </div>
            </Col>
            <Col md={3}>
              <div className="card text-center h-100 shadow-sm">
                <div className="card-body">
                  <h3>üçï</h3>
                  <h5>Pizzas</h5>
                  <p>Cheesy delights</p>
                  <Button as={Link} to="/shop/pizza" variant="outline-primary">
                    Browse
                  </Button>
                </div>
              </div>
            </Col>
            <Col md={3}>
              <div className="card text-center h-100 shadow-sm">
                <div className="card-body">
                  <h3>ü•§</h3>
                  <h5>Drinks</h5>
                  <p>Refreshing beverages</p>
                  <Button as={Link} to="/shop/drink" variant="outline-primary">
                    Browse
                  </Button>
                </div>
              </div>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
}

// THEN EXPORT IT:
export default Home;
