import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { products } from './../products'; // Import your local products
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import './../App.css';

function Shop() {
  const { category } = useParams();
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState('featured');

  useEffect(() => {
    fetchProducts();
  }, [category]);

  const fetchProducts = async () => {
    setLoading(true);
    try {
      // First try to fetch from API
      const response = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/products`);
      
      let allProducts = [];
      if (response.data && response.data.length > 0) {
        allProducts = response.data;
      } else {
        // If API fails, use local products
        allProducts = products;
      }
      
      // Filter products by category if specified
      let filtered = category 
        ? allProducts.filter(product => product.category === category)
        : allProducts;
      
      // Sort products
      filtered = sortProducts(filtered, sortBy);
      
      setFilteredProducts(filtered);
    } catch (error) {
      console.error('API Error, using local products:', error);
      // Use local products on error
      let filtered = category 
        ? products.filter(product => product.category === category)
        : products;
      
      filtered = sortProducts(filtered, sortBy);
      setFilteredProducts(filtered);
    } finally {
      setLoading(false);
    }
  };

  const sortProducts = (productList, sortOption) => {
    const sorted = [...productList];
    
    switch (sortOption) {
      case 'price-low':
        return sorted.sort((a, b) => a.price - b.price);
      case 'price-high':
        return sorted.sort((a, b) => b.price - a.price);
      case 'name':
        return sorted.sort((a, b) => a.name.localeCompare(b.name));
      case 'featured':
      default:
        // Featured first, then alphabetical
        return sorted.sort((a, b) => {
          if (a.featured && !b.featured) return -1;
          if (!a.featured && b.featured) return 1;
          return a.name.localeCompare(b.name);
        });
    }
  };

  const handleSortChange = (e) => {
    const newSort = e.target.value;
    setSortBy(newSort);
    const sorted = sortProducts(filteredProducts, newSort);
    setFilteredProducts(sorted);
  };

  const handleAddToCart = async (product) => {
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        toast.error('Please login to add items to cart!');
        return;
      }
      
      await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/cart`,
        { productId: product._id, quantity: 1 },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          } 
        }
      );
      toast.success(`${product.name} added to cart! üõí`);
      
      // Update local storage for cart count
      updateLocalCart(product);
      
    } catch (error) {
      console.error('Add to cart error:', error);
      // Fallback to localStorage for demo
      toast.success(`${product.name} added to cart (demo mode)! üõí`);
      updateLocalCart(product);
    }
  };

  const updateLocalCart = (product) => {
    const cartItems = JSON.parse(localStorage.getItem('cartItems') || '[]');
    const existingItem = cartItems.find(item => item.id === product._id);
    
    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      cartItems.push({
        id: product._id,
        name: product.name,
        price: product.price,
        quantity: 1,
        image: product.image
      });
    }
    
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  };

  const getCategoryTitle = () => {
    if (!category) return 'All Products';
    
    const categoryNames = {
      'donut': 'Donuts üç©',
      'fancies': 'Fancies üéÇ',
      'pizza': 'Pizza üçï',
      'beverage': 'Beverages ‚òï',
      'drink': 'Drinks ü•§'
    };
    
    return categoryNames[category] || `${category.charAt(0).toUpperCase() + category.slice(1)}`;
  };

  return (
    <div className="shop-page">
      <Container>
        <div className="shop-header">
          <h1>{getCategoryTitle()}</h1>
          <p>Discover our delicious {category ? category.toLowerCase() : 'products'}</p>
        </div>
        
        {/* Categories Navigation */}
        <div className="categories-nav">
          <a 
            href="/shop" 
            className={`category-tab ${!category ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop';
            }}
          >
            <span className="category-icon">üçΩÔ∏è</span>
            <span>All Products</span>
          </a>
          <a 
            href="/shop/donut" 
            className={`category-tab ${category === 'donut' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/donut';
            }}
          >
            <span className="category-icon">üç©</span>
            <span>Donuts</span>
          </a>
          <a 
            href="/shop/fancies" 
            className={`category-tab ${category === 'fancies' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/fancies';
            }}
          >
            <span className="category-icon">üéÇ</span>
            <span>Fancies</span>
          </a>
          <a 
            href="/shop/pizza" 
            className={`category-tab ${category === 'pizza' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/pizza';
            }}
          >
            <span className="category-icon">üçï</span>
            <span>Pizza</span>
          </a>
          <a 
            href="/shop/beverage" 
            className={`category-tab ${category === 'beverage' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/beverage';
            }}
          >
            <span className="category-icon">‚òï</span>
            <span>Beverages</span>
          </a>
          <a 
            href="/shop/drink" 
            className={`category-tab ${category === 'drink' ? 'active' : ''}`}
            onClick={(e) => {
              e.preventDefault();
              window.location.href = '/shop/drink';
            }}
          >
            <span className="category-icon">ü•§</span>
            <span>Drinks</span>
          </a>
        </div>

        {/* Sort Options */}
        <Row className="mb-4">
          <Col md={6}></Col>
          <Col md={6} className="text-md-end">
            <select 
              value={sortBy}
              onChange={handleSortChange}
              className="sort-select"
            >
              <option value="featured">Sort by: Featured</option>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="name">Name: A to Z</option>
            </select>
          </Col>
        </Row>

        {loading ? (
          <div className="text-center py-5">
            <div className="loading-spinner"></div>
            <p className="mt-3">Loading delicious treats...</p>
          </div>
        ) : filteredProducts.length === 0 ? (
          <div className="text-center py-5">
            <h4>No products found in this category</h4>
            <p className="text-muted">Check back later for new items!</p>
          </div>
        ) : (
          <>
            <div className="product-count mb-4">
              Showing {filteredProducts.length} {category ? `${category}` : 'products'}
            </div>
            
            <Row xs={1} md={2} lg={3} xl={4} className="g-4">
              {filteredProducts.map(product => (
                <Col key={product._id}>
                  <div className="product-card-shop">
                    <div className="product-image-container">
                      <img 
                        src={product.image} 
                        alt={product.name}
                        className="product-image-shop"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = "https://images.unsplash.com/photo-1551106652-a5bcf4b29ab6?auto=format&fit=crop&w=500";
                        }}
                      />
                      {product.featured && (
                        <span className="featured-badge">FEATURED</span>
                      )}
                      {product.tags && product.tags.includes('BEST SELLER') && (
                        <span className="best-seller-badge">BEST SELLER</span>
                      )}
                    </div>
                    <div className="product-info-shop">
                      <h3>{product.name}</h3>
                      <p className="product-description-shop">{product.description}</p>
                      
                      <div className="product-tags">
                        {product.tags && product.tags.map((tag, index) => (
                          <span key={index} className="product-tag">{tag}</span>
                        ))}
                      </div>
                      
                      <div className="product-footer-shop">
                        <span className="product-price-shop">${product.price}</span>
                        <button 
                          className="add-to-cart-btn-shop"
                          onClick={() => handleAddToCart(product)}
                        >
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  </div>
                </Col>
              ))}
            </Row>
          </>
        )}
      </Container>
    </div>
  );
}

export default Shop;
